1) Updated save-pdfs-batch.js
Save/replace as save-pdfs-batch.js

const fs = require('fs');
const path = require('path');
const puppeteer = require('puppeteer');
const csv = require('csvtojson');

const templates = {
  onepager: 'pilot-onepager.html',
  sow: 'pilot-sow.html'
};

function sanitize(s) {
  if (!s) return 'customer';
  return s.replace(/[^a-z0-9_\-\.]/gi, '_').replace(/_+/g, '_').toLowerCase();
}

async function loadCustomers() {
  const csvPath = path.join(__dirname, 'customers.csv');
  const jsonPath = path.join(__dirname, 'customers.json');

  if (fs.existsSync(csvPath)) {
    console.log('Loading customers from customers.csv');
    const rows = await csv().fromFile(csvPath);
    // Normalize rows: ensure fields exist and parse templates into array
    return rows.map(r => ({
      customer_name: r.customer_name || r.name || '',
      customer_company: r.customer_company || r.company || '',
      date: r.date || '',
      calendly_link: r.calendly_link || r.calendly || '',
      pilot_duration: r.pilot_duration || '',
      pilot_scope: r.pilot_scope || '',
      pilot_success_metric: r.pilot_success_metric || '',
      // templates: accept "onepager;sow" or "onepager,sow" or "onepager" strings
      templates: (r.templates || r.template || '')
        .split(/[,;]+/)
        .map(s => s.trim().toLowerCase())
        .filter(Boolean)
    }));
  } else if (fs.existsSync(jsonPath)) {
    console.log('Loading customers from customers.json');
    return JSON.parse(fs.readFileSync(jsonPath, 'utf8'));
  } else {
    throw new Error('No customers.csv or customers.json found in working directory.');
  }
}

(async () => {
  let customers;
  try {
    customers = await loadCustomers();
  } catch (err) {
    console.error('Error loading customers:', err.message);
    process.exit(1);
  }

  // Optional global defaults
  const GLOBAL = {
    YOUR_NAME: process.env.YOUR_NAME || 'Your Name',
    YOUR_EMAIL: process.env.YOUR_EMAIL || 'you@startup.com',
  };

  const CONCURRENCY = parseInt(process.env.CONCURRENCY || '3', 10);

  const browser = await puppeteer.launch({ args: ['--no-sandbox', '--disable-setuid-sandbox'] });

  try {
    // Build tasks
    const tasks = [];
    for (const customer of customers) {
      const data = {
        CUSTOMER_NAME: customer.customer_name || '',
        CUSTOMER_COMPANY: customer.customer_company || '',
        DATE: customer.date || '',
        CALENDLY_LINK: customer.calendly_link || '',
        YOUR_NAME: customer.your_name || GLOBAL.YOUR_NAME,
        YOUR_EMAIL: customer.your_email || GLOBAL.YOUR_EMAIL,
        PILOT_DURATION: customer.pilot_duration || '',
        PILOT_SCOPE: customer.pilot_scope || '',
        PILOT_SUCCESS_METRIC: customer.pilot_success_metric || ''
      };
      for (const tpl of (customer.templates || [])) {
        // accept synonyms
        const key = tpl.toLowerCase();
        if (key === 'onepager' || key === 'one-pager' || key === 'one_page') tasks.push({ tpl: 'onepager', data });
        else if (key === 'sow' || key === 'pilot-sow' || key === 's-o-w') tasks.push({ tpl: 'sow', data });
        else console.warn(`Unrecognized template "${tpl}" for customer ${data.CUSTOMER_NAME} - skipping`);
      }
    }

    console.log(`Built ${tasks.length} render tasks. Running with concurrency=${CONCURRENCY}`);

    for (let i = 0; i < tasks.length; i += CONCURRENCY) {
      const batch = tasks.slice(i, i + CONCURRENCY);
      await Promise.all(batch.map(async task => {
        const tplFile = templates[task.tpl];
        const tplPath = path.join(__dirname, tplFile);
        if (!fs.existsSync(tplPath)) {
          console.warn(`Template file missing: ${tplFile}. Skipping.`);
          return;
        }

        let html = fs.readFileSync(tplPath, 'utf8');

        // Replace tokens for all expected keys
        const replacements = {
          CUSTOMER_NAME: task.data.CUSTOMER_NAME,
          CUSTOMER_COMPANY: task.data.CUSTOMER_COMPANY,
          DATE: task.data.DATE,
          CALENDLY_LINK: task.data.CALENDLY_LINK,
          YOUR_NAME: task.data.YOUR_NAME,
          YOUR_EMAIL: task.data.YOUR_EMAIL,
          PILOT_DURATION: task.data.PILOT_DURATION,
          PILOT_SCOPE: task.data.PILOT_SCOPE,
          PILOT_SUCCESS_METRIC: task.data.PILOT_SUCCESS_METRIC
        };

        Object.keys(replacements).forEach(key => {
          const val = replacements[key] || '';
          const re = new RegExp(`{{\\s*${key}\\s*}}`, 'g');
          html = html.replace(re, val);
        });

        const friendly = sanitize(task.data.CUSTOMER_NAME || task.data.CUSTOMER_COMPANY || 'customer');
        const tmpHtml = path.join(__dirname, `.tmp_${task.tpl}_${friendly}.html`);
        fs.writeFileSync(tmpHtml, html, 'utf8');

        const page = await browser.newPage();
        try {
          await page.goto('file://' + tmpHtml, { waitUntil: 'networkidle0' });

          const outName = `${task.tpl === 'sow' ? 'Startup-Pilot-SOW' : 'Startup-Pilot'}-${friendly}.pdf`;
          const outPath = path.join(__dirname, outName);

          await page.pdf({
            path: outPath,
            format: 'Letter', // change to 'A4' if you prefer
            printBackground: true,
            margin: { top: '12mm', bottom: '12mm', left: '10mm', right: '10mm' }
          });

          console.log('Generated PDF:', outPath);
        } catch (err) {
          console.error('Render error for', friendly, err);
        } finally {
          await page.close();
          try { fs.unlinkSync(tmpHtml); } catch (e) {}
        }
      }));
    }

    console.log('All tasks complete.');
  } catch (err) {
    console.error('Unexpected error:', err);
  } finally {
    await browser.close();
  }
})().catch(e => {
  console.error('Fatal error:', e);
  process.exit(1);
});

